import React, {Component} from "react";
import "./vocabulary.css";

class Vocabulary extends Component {
    constructor(props) {
        super(props);

        this.setState = {
            listVocabulary: []
        };
    }

    componentDidMount() {}

    getVocabularyByTopicId = async id => {
        await fetch("vocabulary/get-vocabularies-by-topic-id/1")
            .then(response => response.json())
            .then(data => this.setState({listVocabulary: data}));
    };

    render() {

        var {listVocabulary} = this.state;

        var elmVoca = listVocabulary.map((voc, index) => {
            return (
                <div className="card col-4" key={index}>
                    <img
                        className="card-img-top"
                        src={"/images/Vocabulary/" + voc.word + ".png"}
                        alt={voc.word}/>

                    <div className="card-body">
                        <h5 className="card-title">{voc.word}</h5>
                        <p className="card-text">
                            {voc.meaning}
                        </p>
                    </div>
                </div>
            );
        });

        return (
            <React.Fragment>
                <br/>

                <div className="selectedTopic" align="center">
                    <button>topic1</button>
                </div>
                <hr/>
                <h3>Selected Topic</h3>
                {elmVoca}
            </React.Fragment>
        );
    }
}

export default Vocabulary;
